const FormData = require('form-data');
const axios    = require('axios');


const item = $input.all()[0];


const attachment = item.binary?.AudioFile;
if (!attachment) {
  throw new Error('Aucun binaire "AudioFile" trouvé');
}

// Prépare le form-data
const form = new FormData();
form.append(
  'file',
  // Buffer à partir du base64
  Buffer.from(attachment.data, 'base64'),
  {
    filename:    attachment.fileName,  
    contentType: attachment.mimeType,  
  }
);

// Appel à ton API Flask
const resp = await axios.post(
  'http://host.docker.internal:5000/transcribe', 
  form,
  {
    headers: {

      ...form.getHeaders(),
    },

    maxContentLength: Infinity,
    maxBodyLength:    Infinity,
    timeout:          600_000, 
  }
);

// Retourne le JSON de transcritpion
return [
  {
    json: resp.data,
  }
];

