// Get the raw string output from the previous node
// Replace 'llm_response_field' with the actual name of the field containing the text
const rawResponse = $('Basic LLM Chain').first().json.text;
// This regular expression finds the <think> block and any whitespace after it
// It's designed to be robust even if the think block has content or newlines
const regex = /<think>[\s\S]*?<\/think>\s*/;

// Remove the matched <think> block from the string
const cleanedString = rawResponse.replace(regex, '');

// Now that we have a clean JSON string, parse it into a JavaScript object
const jsonData = JSON.parse(cleanedString);

// Return the parsed data in a new field called 'structuredData'
// This makes it easy to reference in the next node
return {
  structuredData: jsonData
};

const crypto = require('crypto');

const SECRET_KEY = $env['GCE_SHARED_SECRET_KEY'];
const IV = $env['GCE_SHARED_IV'];
const ALGORITHM = 'aes-256-cbc';

if (!SECRET_KEY || SECRET_KEY.length !== 32) {
  throw new Error("ERREUR: GCE_SHARED_SECRET_KEY est manquante ou n'a pas une longueur de 32 caractères.");
}
if (!IV || IV.length !== 16) {
  throw new Error("ERREUR: GCE_SHARED_IV est manquante ou n'a pas une longueur de 16 caractères.");
}
// --- FIN BLOC DE DEBUG ---

const item = items[0].json;
const encryptedData = item.sec1;



if (!encryptedData) {

  item.decrypted_password = null;
  return [item];
}

try {
  const decipher = crypto.createDecipheriv(ALGORITHM, Buffer.from(SECRET_KEY), Buffer.from(IV));

  let decrypted = decipher.update(encryptedData, 'base64', 'utf8');
  decrypted += decipher.final('utf8');


  item.decrypted_password = decrypted;

} catch (error) {

  throw error;
}

return [item];

const nodemailer = require('nodemailer');
const imaps = require('imap-simple');
// On importe la bonne fonction depuis le module, comme corrigé précédemment
const { createMimeMessage } = require('mail-mime-builder');

// --- RÉCUPÉRATION DES DONNÉES ENTRANTES ---
const item = $input.first();
// ===================================================================
//  EMAIL TEMPLATE (Basé sur le Look & Feel du PDF)
// ===================================================================
const emailTemplate = `
<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>__SUJET_PLACEHOLDER__</title>
    <style>
