// --- LOGIQUE PRINCIPALE ---
try {

  let jsonData = item.json;

 const destinataire =  $('Clean and Parse LLM Response').first().json.structuredData['email client'];
  const sujet =  $('Clean and Parse LLM Response').first().json.structuredData.sujet;
  const corpsGenereParLlm =  $('Clean and Parse LLM Response').first().json.structuredData['intro to client'] + '<br>' + $('Clean and Parse LLM Response').first().json.structuredData.data;




// On ajoute le bouton au corps de l'e-mail
const corpsAvecBouton = corpsGenereParLlm  ;

// On injecte le tout dans le template final
let finalHtml = emailTemplate.replace('__BODY_PLACEHOLDER__', corpsAvecBouton);
finalHtml = finalHtml.replace('__SUJET_PLACEHOLDER__', sujet);

  const smtpUser = jsonData.Email;
  const smtpPass = jsonData.decrypted_password;
  const mailHost = "eecie.ca";

  const mailOptions = {
    from: `"Excellence Executive Cie" <${smtpUser}>`, // From Name amélioré
    to: destinataire,
    subject: sujet,
    html: finalHtml,

  };


  const authConfig = { smtpUser, smtpPass, mailHost };
  let result;


    result = await sendAndSaveEmail(mailOptions, authConfig, 'Sent');

  return [{ json: result }];

} catch (error) {
  console.error(error);
  throw error;
}

// --- FONCTIONS D'AIDE (Aucun changement ici, à conserver telles quelles) ---

async function sendAndSaveEmail(mailOptions, authConfig, sentMailboxName) {
  const transporter = nodemailer.createTransport({
    host: authConfig.mailHost, port: 587, secure: false,
    auth: { user: authConfig.smtpUser, pass: authConfig.smtpPass },
    tls: { rejectUnauthorized: false }
  });

  const info = await transporter.sendMail(mailOptions);
  console.log(`Email envoyé avec succès à ${mailOptions.to}. MessageId: ${info.messageId}`);

  try {
    await saveToMailbox(mailOptions, authConfig, sentMailboxName);
    return { success: true, message: `Mail envoyé à ${mailOptions.to} et sauvegardé dans '${sentMailboxName}'`, messageId: info.messageId };
  } catch (imapError) {
    console.error(`L'email a été envoyé, mais la sauvegarde dans '${sentMailboxName}' a échoué:`, imapError.message);
    return { success: true, message: `Mail envoyé à ${mailOptions.to} mais échec de la sauvegarde.`, warning: imapError.message, messageId: info.messageId };
  }
}

async function saveToMailbox(mailOptions, authConfig, mailboxName) {
  const msg = createMimeMessage();
  msg.setSender(mailOptions.from);
  msg.setTo(mailOptions.to);
  msg.setSubject(mailOptions.subject);

  msg.addMessage({ contentType: 'text/html', data: mailOptions.html });

  if (mailOptions.attachments && mailOptions.attachments.length > 0) {
    const attachment = mailOptions.attachments[0];
    const attachmentBase64 = attachment.content.toString('base64');
    msg.addAttachment({
      filename: attachment.filename,
      contentType: attachment.contentType,
      data: attachmentBase64,
      headers: { 'Content-Transfer-Encoding': 'base64' }
    });
  }

  const rawEmail = msg.asRaw();
  const flags = mailboxName === 'Drafts' ? ['\\Draft', '\\Seen'] : ['\\Seen'];

  const config = {
    imap: {
      user: authConfig.smtpUser, password: authConfig.smtpPass,
      host: authConfig.mailHost, port: 993, tls: true,
      tlsOptions: { rejectUnauthorized: false }
    }
  };

  let connection;
  try {
    connection = await imaps.connect(config);
    await connection.append(rawEmail, { mailbox: mailboxName, flags: flags });
    connection.end();
    return { success: true, message: `Email sauvegardé avec succès dans '${mailboxName}'` };
  } catch (err) {
    if (connection) connection.end();
    throw new Error(`Erreur IMAP: Impossible de sauvegarder dans '${mailboxName}'. Détails: ${err.message}`);
  }
}

