// This code requires luxon. Make sure it's enabled in your instance settings.
const luxon = require('luxon');

// Get the full opportunity object from the first step
const opp = $('Get opportunité').item.json;

// Get the confirmed start date from the webhook trigger
const firstDate = luxon.DateTime.fromISO($('Webhook').item.json.body.items[0].Date_Heure_RDV);

// This will hold the list of dates to be created
const datesToCreate = [];

// Calculate the end of our 3-month planning window from the first confirmed appointment
const threeMonthsFromStart = firstDate.plus({ months: 3 });

// Get the contract's end date, if it exists
let contractEndDate = opp.Date_Fin_Recurrence ? luxon.DateTime.fromISO(opp.Date_Fin_Recurrence) : null;

// Determine the true end date: the sooner of the 3-month window or the contract end date
let effectiveEndDate = threeMonthsFromStart;
if (contractEndDate && contractEndDate < threeMonthsFromStart) {
  effectiveEndDate = contractEndDate;
}

// Prepare for the loop
let currentDate = firstDate;
// Get the frequency and interval from the opportunity
const intervalUnit = opp.Frequence.value.toLowerCase(); // 'jours', 'Semaines', 'Mois', etc.
const intervalValue = opp.Intervalle;

// Loop to generate dates until we reach the effective end date
while (currentDate <= effectiveEndDate) {
  // Add the date to our output list in the format n8n expects
  datesToCreate.push({ json: { date: currentDate.toISO() } });

  // Increment the date for the next iteration
  currentDate = currentDate.plus({ [intervalUnit]: intervalValue });
}

// Return the array of items, each containing a date
return datesToCreate;
